mapper.insert:
    INSERT INTO exam.student (
sname, 
department_id
    )
    VALUES (
#{sname}, 
#{departmentId}
    );

mapper.update:
    UPDATE exam.student
    SET
sname = #{sname}, 
department_id = #{departmentId}
    WHERE student.id = #{id}

mapper.select:
SELECT * FROM exam.student
student.id = #{id}
AND sname LIKE "%"#{sname}"%" AND department_id LIKE "%"#{departmentId}"%"
